var cssnano = require('cssnano');

var autoprefixer = require('autoprefixer');

var postcss = require('postcss');

var path = require('path');

var escapeStringRegexp = require('escape-string-regexp');

function getSubDir(file, refDir) {
  var subDir = path.dirname(path.relative(refDir, file)).replace(/\\/g, '/');
  return subDir !== '.' && !subDir.includes('..') ? subDir : null;
}

function getLanguage(file, refDir) {
  var separator = escapeStringRegexp(path.sep);
  var baseDir = escapeStringRegexp(path.basename(refDir));
  var regexSubFolder = new RegExp("".concat(baseDir).concat(separator, "(.*?)").concat(separator, ".*"));
  var match = regexSubFolder.exec(file); // Avoid misinterpreting subdirectory as language

  var env = require('./env.js');

  if (match && env.translation.enabled) {
    var enabledLanguages = env.translation.enabledLanguages().map(function (language) {
      return language.tag;
    });

    if (enabledLanguages.indexOf(match[1]) !== -1) {
      return match[1];
    }
  }

  return null;
}

function isSeparateCss(file, separateDirs) {
  if (!separateDirs) {
    return false;
  }

  for (var i = 0; i < separateDirs.length; i++) {
    if (file.includes(separateDirs[i])) {
      return true;
    }
  }

  return false;
}

function minifyCss(cssContent) {
  return cssnano.process(cssContent, {
    preset: 'default',
    zindex: false,
    from: undefined
  }).then(function (result) {
    return result.css;
  });
}

function autoPrefixCss(cssContent) {
  return postcss([autoprefixer]).process(cssContent, {
    from: undefined
  }).then(function (result) {
    return result.css;
  });
}

